package bangyoon.java.eclipse;

public class String {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
		System.out.println('문'); //문자는 '' or ""
		System.out.println("문"); 
		System.out.println("문자열"); //문자열은 ""
		
		System.out.println("나는"+' '+"방윤입니다."); //문자열 연산 더하기
		System.out.println("방윤은 말했다 \"나는 방윤입니다.\""); //Escape \"
		System.out.println("HTML\nCSS\nJacaScript\n"); //여러줄 표시 \n
	}

}
